import React from "react";
import axios from 'axios'
import AddLocation from "./location-components/AddLocation";
import Map from "./map-components/Map";
import mapStyle from '../mapStyles';



export default class Home extends React.Component {

    constructor(props){
        super(props);
        this.state={
            map:{},
            allLocations:[],
            visibleLocations:[],
            markers: [],
            ready: false,
        }
    }

    componentDidMount(){
        this.getAllLocations();
        //this.renderMap();    
    }

    getAllLocations(){
        axios.get("http://localhost:5000/api/locations")
                .then(response=>{
                    this.setState({
                        allLocations: response.data,
                        visibleLocations: response.data,
                        ready:true,
                    })
                })
                .catch(err=>{
                console.log(err)
                })
        }


    loadScript = (url)=>{
        let index = window.document.getElementsByTagName('script')[0];
        let script = window.document.createElement('script');
        script.src = url;
        script.async = true;
        script.defer = true;
        index.parentNode.insertBefore(script,index);
    }

    renderMap=()=>{
        this.loadScript(`https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_KEY}&callback=initMap`);
        window.initMap = this.initMap;
    }

    addMarker = () => {
        for(let locations = 0; locations < this.props.allLocations.length; locations++){
            const lat = this.props.allLocations[locations].lat;
            const lng = this.props.allLocations[locations].lng;

            var marker = new window.google.maps.Marker({
                position: {lat: lat, lng: lng},
                map: this.state.map
            });

            let markersCopy = [...this.state.markers] 

            markersCopy.push(marker);
            this.setState({
                markers:markersCopy
            });
        }
    }

    initMap = () =>  {
        var myLatlng = {lat:25.7617,lng:-80.1918}
        let map = new window.google.maps.Map(document.getElementById('map'), {
            center: myLatlng,
            zoom: 9,
            styles: mapStyle,
            disableDoubleClickZoom: true,
        });
        this.setState({
            map:map,
        })

        this.addMarker();
    }

        setMapOnAll = (map) => {
            for (var i = 0; i < this.markers.length; i++) {
            this.markers[i].setMap(map);
            }
    }
    render(){
        if(this.state.ready){
            console.log('This is how many markers we have so far',this.state.markers.length)
        }
        return (
            <section>
                <h1> Dashboard</h1>

                <AddLocation getAllLocations={this.props.getAllLocations} ></AddLocation>
                {this.state.ready &&
                <Map ready={this.state.ready} allLocations={this.state.visibleLocations}></Map>}
            </section>
        )
    }
}
