import React from 'react'

export default class LocationDetails extends React.Component {
  constructor(props){
    super(props);
  }


  renderLocationDetails=()=>{
    const location= this.props.visibleLocations.filter(location => location._id === this.props.match.params.id);
    return(
      <div className="location-details-container">
          <h1>Location Details</h1>
        <h1>{location.title}</h1>
        <h4>{location.description}</h4>

      </div>
    )
  }

  render() {

    console.log('Location details is rendered')
    console.log(this.renderLocationDetails())

    return (
      <div>
        {this.renderLocationDetails}
      </div>
    )
  }
}
