import React from "react";
import axios from 'axios'
import AddLocation from "./location-components/AddLocation";
import Map from "./map-components/Map";



export default class Home extends React.Component {

    constructor(props){
        super(props);
        this.state={
            allLocations:[],
            visibleLocations:[],
            ready: false,
        }
    }

    componentDidMount(){
        this.getAllLocations();
    }


    getAllLocations(){
        axios.get("http://localhost:5000/api/locations")
                .then(response=>{
                this.setState({
                    allLocations: response.data,
                    visibleLocations: response.data,
                    ready:true,
                })
                })
                .catch(err=>{
                console.log(err)
                })
        }

    loadScript = (url)=>{
        let index = window.document.getElementsByTagName('script')[0];
        let script = window.document.createElement('script');
        script.src = url;
        script.async = true;
        script.defer = true;
        index.parentNode.insertBefore(script,index);
    }


    render(){
        return (
            <section>
                <h1> Home Page </h1>
                <p> Welcome to the best app ever! ❤️ </p>
                <AddLocation getAllLocations={this.props.getAllLocations} ></AddLocation>
                {this.props.ready &&
                <Map ready={this.state.ready} allLocations={this.state.visibleLocations}></Map>}
            </section>
        )
    }
}
