import React from 'react';
import axios from 'axios';

export default class AddLocation extends React.Component {

  constructor(props){
    super(props);
    this.state={
                title:'',
                description:'',
                lat:'',
                lng:'',
                imageUrl:''
              }
  }



  fileSelectedHandler = (e) =>{
    this.setState({
      selectedFile: e.target.files[0],
    })
    console.log(e.target.files[0])
  }

  fileUploadHandler = (e) =>{
    console.log('The file to be uploaded is: ', e.target.files[0]);
    const uploadData = new FormData();
    uploadData.append('imageUrl', e.target.files[0])

    axios.post('http://localhost:5000/api/uploads',uploadData)
          .then(res =>{
            //********After file is uploaded this line takes time to execute and if save is hit before the same image is save in MongoDb twice
            //*************************** */
            //*************************** */
            this.setState({imageUrl: res.data.secure_url});
          })
          .catch(err=>{
            console.log('Error while uploading the file: ', err);
          });
  }

  render() {
    return (
      <div className="add-location-form">
        <form onSubmit={(e) =>this.props.formHandle(e)}>
          <div>
              <label>Title</label>
              <input onChange={this.props.inputHandle} type="text" name="title" value={this.props.title}/>
          </div>
          <div>
              <label>Description</label>
              <input onChange={this.props.inputHandle} type="text" name="description" value={this.props.description}/>
          </div>
          <div>
              <label>Latitude</label>
              <input onChange={this.props.inputHandle} type="number" name="lat" value={this.props.lat}/>
          </div>
          <div>
              <label>Longitude</label>
              <input onChange={this.props.inputHandle} type="number" name="lng" value={this.props.lng}/>
          </div>
          <div>
            <input type="file" onChange={this.fileSelectedHandler}/>
            <button onClick={this.fileUploadHandler}>Save File</button>
          </div>
          <button>Save Location</button>
        </form> 
      </div>
    )
  }
}
