import React from "react";
import axios from 'axios'
import AddLocation from "./location-components/AddLocation";
import Map from "./map-components/Map";
import mapStyle from '../mapStyles';



export default class Home extends React.Component {

    constructor(props){
        super(props);
        this.state={
            map:{},
            allLocations:[],
            visibleLocations:[],
            markers: [],
            ready: false,
            title:'',
            description:'',
            lat:'',
            lng:'',
        }
    }



    componentDidMount(){
        this.getAllLocations();
    }

    inputHandle=(e)=>{
        const name = e.target.name;
        const value = e.target.value;

        this.setState({
            [name]: value,
        })
    }

    inputCoordinatesHandle=(e)=>{
        this.setState({
            lat: e.latLng.lat(),
            lng:e.latLng.lng()
        })
    }

    formHandle=(e)=>{
        e.preventDefault()
        const newLocation = {
            title:this.state.title,
            description: this.state.description,
            lat: this.state.lat,
            lng: this.state.lng
        }

        axios.post('http://localhost:5000/api/locations',newLocation)
                .then(response=>{
                    this.getAllLocations();
                })
                .catch(err=>{
                    console.log(err)
                })
    }

    getAllLocations=()=>{
        axios.get("http://localhost:5000/api/locations")
                .then(response=>{
                    this.setState({
                        allLocations: response.data,
                        visibleLocations: response.data,
                        ready:true,
                    },()=>{
                        this.updateMap();
                    })
                })
                .catch(err=>{
                console.log(err)
                })
    }

    render(){
        return (
            <section>
                {this.state.ready &&
                                        <Map 
                                            allLocations={this.state.allLocations}
                                            inputCoordinatesHandle={this.inputCoordinatesHandle}
                                        />
                }
                <AddLocation 
                    inputHandle={this.inputHandle} 
                    formHandle={this.formHandle} 
                    getAllLocations={this.getAllLocations} 
                    title={this.state.title} 
                    description={this.state.description}
                    lat={this.state.lat} 
                    lng={this.state.lng}>
                </AddLocation>
            </section>
        )
    }
}
