import React from 'react';
import mapStyle from './mapStyles';
import ReactDOMServer from "react-dom/server";

import './App.css';
import axios from "axios";
import { Switch, Route } from "react-router-dom";
import Signup from "./components/user-pages/Signup";
import Home from "./components/Home";
import Login from "./components/user-pages/Login";
import Navbar from './components/Navbar'
import LocationDetails from './components/LocationDetails'
import Sidebar from './components/Sidebar'



class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      currentUser: null,
      allLocations:[],
      visibleLocations:[],
      ready: false,
    }
  }

  componentDidMount(){
    this.getAllLocations()
    this.requestUserToDB(); 
  }




  renderMap=()=>{
    this.loadScript(`https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_KEY}&libraries=places&callback=initMap`);
    window.initMap = this.initMap;
  }

  loadScript=(url)=>{
    let index = window.document.getElementsByTagName('script')[0];
    let script = window.document.createElement('script');
    script.src = url;
    script.async = true;
    script.defer = true;
    index.parentNode.insertBefore(script,index);
  }

  initMap = () => {

    let map = new window.google.maps.Map(document.getElementById('map'),{styles: mapStyle,disableDoubleClickZoom: true });

    navigator.geolocation.getCurrentPosition(function(position) {
      // Center on user's current location if geolocation prompt allowed
      var initialLocation = new window.google.maps.LatLng(position.coords.latitude, position.coords.longitude-0.7);
      map.setCenter(initialLocation);
      map.style = mapStyle;
      map.setZoom(9);
    }, function(positionError) {
      // User denied geolocation prompt - default to Chicago
      map.setCenter(new window.google.maps.LatLng(35.74750449199224, -86.7035884912965));
      map.style=mapStyle;
      map.setZoom(5);
});

    this.setState({
      map:map,
    })

    map.addListener('dblclick',(e)=>{
      this.props.addLocationToggle();
      this.props.inputCoordinatesHandle(e);
  })

    this.addMarker();
  }


  addMarker = () => {
    for(let locations = 0; locations < this.props.visibleLocations.length; locations++){
        const lat = this.props.visibleLocations[locations].lat;
        const lng = this.props.visibleLocations[locations].lng;

        let marker = new window.google.maps.Marker({
          position: {lat: lat, lng: lng},
          map: this.state.map
        });

        let infowindow = new window.google.maps.InfoWindow({
          content:'',
        });

        marker.addListener('click', ()=>{
          const content = ReactDOMServer.renderToString(this.InfoWindowContent(this.props.visibleLocations[locations]));

          infowindow.setContent(content);
          infowindow.open(this.map, marker);
        });


        let markersCopy = [...this.state.markers] 

        markersCopy.push(marker);
        this.setState({
          markers:markersCopy
        });
    }
  }


  getAllLocations=()=>{
    axios.get("http://localhost:5000/api/locations")
            .then(response=>{
                this.setState({
                    allLocations: response.data,
                    visibleLocations: response.data,
                    ready:true,
                })
            })
            .catch(err=>{
            console.log(err)
            })
}

  requestUserToDB(){
    axios.get("http://localhost:5000/api/checkuser", { withCredentials: true })
        .then(response => {
          console.log('Im getting user')
          const { userDoc } = response.data;
          this.syncCurrentUSer(userDoc);
          console.log('Check user in app: ', userDoc)
        })
        .catch(err=>{
          console.log("Err while getting the user from the checkuser route: ", err)
        })
  }

  logout=()=>{
    axios.post('http://localhost:5000/api/logout',{})
          .then(response=>{
            console.log('This is the data for user###########################',response.data)
            this.setState({
              currentUser:response.userDoc,
            })
            this.props.history.push(`/login`)
          }) 
          .catch(err=>{
            console.log(err);
          })
  }

  syncCurrentUSer(user){
    this.setState({ currentUser: user })
  }

  render(){

    return (
      <div>
        <Navbar 
          currentUser = { this.state.currentUser }  
          logout={this.logout}>
        </Navbar>
        <Sidebar>
        </Sidebar>
        <Switch>
            <Route exact path="/" render={ ()=>< Home
                visibleLocations={this.state.visibleLocations}
                ready={this.state.ready}
                getAllLocations={this.getAllLocations}
            /> }   /> 

            <Route exact path="/signup-page" render = { () => 
                <Signup 
                  currentUser = { this.state.currentUser }   
                  onUserChange = { userDoc => this.syncCurrentUSer(userDoc) }   
                /> 
            }/>

            <Route exact path="/login" render={ (props)=> 
                <Login 
                  {...props} 
                  currentUser = { this.state.currentUser }   
                  onUserChange = { userDoc => this.syncCurrentUSer(userDoc) }  
                /> 
            } /> 

            <Route exact path="/details/:id" render={(props)=>
                < LocationDetails 
                {...props}
                visibleLocations={this.state.visibleLocations}
                ready={this.state.ready}
                />
            }  /> 
        </Switch>
      </div>
    );
  }
}

export default App;
